var btnModalOpen=document.querySelector(".contacts-modal-form-link"),modalWhole=document.querySelector(".modal-connection__layout"),modalFormSection=document.querySelector(".modal-connection"),modalForm=document.querySelector(".connection-form"),btnModalClose=document.querySelector(".connection__close"),login=document.querySelector(".login"),password=document.querySelector(".password"),message=document.querySelector(".message"),wrapper=document.querySelector(".site-wrapper"),sliderElement=document.querySelectorAll(".slider-item"),
  btnSlider=document.querySelectorAll(".slider-btn");function clean(a,c,d){c.forEach(function(b){b.classList.remove("active")});d.forEach(function(b){b.classList.remove("active")});a.classList.remove("wrapper-1");a.classList.remove("wrapper-2");a.classList.remove("wrapper-3")}btnModalOpen.addEventListener("click",function(a){a.preventDefault();modalWhole.classList.add("open");login.focus()});btnModalClose.addEventListener("click",function(){modalWhole.classList.remove("open");modalFormSection.classList.remove("error")});
window.addEventListener("keydown",function(a){27===a.keyCode&&modalWhole.classList.contains("open")&&(a.preventDefault(),modalWhole.classList.remove("open"),modalFormSection.classList.remove("error"))});modalForm.addEventListener("submit",function(a){login.value&&password.value&&message.value||(a.preventDefault(),modalFormSection.classList.remove("error"),modalForm.offsetWidth=modalForm.offsetWidth,modalFormSection.classList.add("error"))});
btnSlider[0].addEventListener("click",function(){clean(wrapper,sliderElement,btnSlider);btnSlider[0].classList.add("active");sliderElement[0].classList.add("active");wrapper.classList.add("wrapper-1")});btnSlider[1].addEventListener("click",function(){clean(wrapper,sliderElement,btnSlider);btnSlider[1].classList.add("active");sliderElement[1].classList.add("active");wrapper.classList.add("wrapper-2")});
btnSlider[2].addEventListener("click",function(){clean(wrapper,sliderElement,btnSlider);btnSlider[2].classList.add("active");sliderElement[2].classList.add("active");wrapper.classList.add("wrapper-3")});
